import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import { createUser } from '../services/api'; // Import createUser
import './UserForm.css';

const UserForm = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ username: '', email: '', password: '' });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await createUser(formData); // Use createUser from api.js
      localStorage.setItem('userId', response.id);
      Swal.fire({ icon: 'success', title: 'Signed Up!', text: 'Welcome to CookSphere!', timer: 1500 });
      navigate('/profile');
    } catch (error) {
      Swal.fire({ icon: 'error', title: 'Signup Failed', text: 'Something went wrong.' });
      console.error('Signup error:', error);
    }
  };

  return (
    <div className="signup-wrapper">
      <div className="signup-card">
        <h2 className="signup-header">Join CookSphere</h2>
        <form onSubmit={handleSubmit} className="signup-form">
          <input name="username" value={formData.username} onChange={handleChange} placeholder="Username" className="signup-input" />
          <input name="email" value={formData.email} onChange={handleChange} placeholder="Email" className="signup-input" />
          <input name="password" type="password" value={formData.password} onChange={handleChange} placeholder="Password" className="signup-input" />
          <button type="submit" className="signup-submit">Sign Up</button>
        </form>
      </div>
    </div>
  );
};

export default UserForm;
