import axios from 'axios';

const API_URL = 'http://localhost:8081/api/users';

export const getUser = (id) => axios.get(`${API_URL}/${id}`).then(res => res.data);
export const updateUser = (id, user) => axios.put(`${API_URL}/${id}`, user).then(res => res.data);
export const deleteUser = (id) => axios.delete(`${API_URL}/${id}`).then(() => {});
export const followUser = (followerId, followingId) => axios.post(`${API_URL}/${followerId}/follow/${followingId}`).then(res => res.data);
export const unfollowUser = (followerId, followingId) => axios.post(`${API_URL}/${followerId}/unfollow/${followingId}`).then(res => res.data);
export const getFollowers = (id) => axios.get(`${API_URL}/${id}/followers`).then(res => res.data);
export const getFollowing = (id) => axios.get(`${API_URL}/${id}/following`).then(res => res.data);
export const getAllUsers = () => axios.get(API_URL).then(res => res.data);

// Placeholder for login (adjust once backend login endpoint is added)
export const loginUser = (credentials) => axios.post(`${API_URL}/login`, credentials).then(res => res.data);
