import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import { createWorkPlan } from '../services/api';
import './WorkPlanForm.css';

const WorkPlanForm = () => {
  const navigate = useNavigate();
  const currentUserId = localStorage.getItem('userId');
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    topics: '',
    startDate: '',
    endDate: '',
    status: 'In Progress',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const workPlan = {
        ...formData,
        userId: currentUserId,
        topics: formData.topics.split(',').map((topic) => topic.trim()), // Convert comma-separated string to array
      };
      await createWorkPlan(workPlan);
      Swal.fire({
        icon: 'success',
        title: 'Work Plan Created!',
        text: 'Your work plan has been created.',
        timer: 1500,
      });
      navigate('/profile');
    } catch (error) {
      Swal.fire({
        icon: 'error',
        title: 'Creation Failed',
        text: 'Something went wrong.',
      });
      console.error('Create work plan error:', error);
    }
  };

  return (
    <div className="workplan-container">
      <div className="workplan-card">
        <h2>Create Work Plan</h2>
        <form onSubmit={handleSubmit} className="workplan-form">
          <input
            name="title"
            value={formData.title}
            onChange={handleChange}
            placeholder="Title (e.g., learn curr dhal  )"
            className="workplan-input"
            required
          />
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            placeholder="Description"
            className="workplan-textarea"
          />
          <input
            name="topics"
            value={formData.topics}
            onChange={handleChange}
            placeholder="Topics (comma-separated, e.g., Variables, Loops)"
            className="workplan-input"
          />
          <input
            name="startDate"
            type="date"
            value={formData.startDate}
            onChange={handleChange}
            className="workplan-input"
            required
          />
          <input
            name="endDate"
            type="date"
            value={formData.endDate}
            onChange={handleChange}
            className="workplan-input"
            required
          />
          <select
            name="status"
            value={formData.status}
            onChange={handleChange}
            className="workplan-input"
          >
            <option value="In Progress">In Progress</option>
            <option value="Completed">Completed</option>
            <option value="Not Started">Not Started</option>
          </select>
          <button type="submit" className="workplan-submit">Create</button>
        </form>
        <button className="workplan-cancel" onClick={() => navigate('/profile')}>
          Cancel
        </button>
      </div>
    </div>
  );
};

export default WorkPlanForm;
