import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import { loginUser } from '../services/api';
import './LoginForm.css';

const LoginForm = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ username: '', password: '' });
  const [errors, setErrors] = useState({});

  // Validation function
  const validateField = (name, value) => {
    let error = '';
    if (name === 'username') {
      if (!value) error = 'Username is required';
      else if (value.length < 3) error = 'Username must be at least 3 characters';
    }
    if (name === 'password') {
      if (!value) error = 'Password is required';
      else if (value.length < 6) error = 'Password must be at least 6 characters';
    }
    return error;
  };

  // Handle input changes with live validation
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    const error = validateField(name, value);
    setErrors((prevErrors) => ({
      ...prevErrors,
      [name]: error,
    }));
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = {
      username: validateField('username', formData.username),
      password: validateField('password', formData.password),
    };

    if (Object.values(newErrors).some((error) => error)) {
      setErrors(newErrors);
      Swal.fire({
        icon: 'error',
        title: 'Validation Error',
        text: 'Please fix the errors in the form.',
      });
      return;
    }

    try {
      const userData = await loginUser(formData); // Call login API
      if (!userData || !userData.userId) {
        throw new Error('Invalid response from server: No user ID found');
      }
      localStorage.setItem('userId', userData.userId); // Store user ID
      Swal.fire({
        icon: 'success',
        title: 'Login Successful!',
        text: `Welcome back, ${userData.username || 'User'}!`,
        timer: 1500,
        showConfirmButton: false,
      }).then(() => {
        navigate('/home'); // Redirect to home
      });
    } catch (error) {
      let errorMessage = 'Invalid username or password. Please try again.';
      if (error.response) {
        if (error.response.status === 401) {
          errorMessage = 'Incorrect username or password.';
        } else if (error.response.status === 404) {
          errorMessage = 'User not found.';
        } else {
          errorMessage = `Server error: ${error.response.status}`;
        }
      }
      Swal.fire({
        icon: 'error',
        title: 'Login Failed',
        text: errorMessage,
      });
      console.error('Login error:', error.message);
      console.error('Error response:', JSON.stringify(error.response?.data, null, 2));
    }
  };

  return (
    <div className="login-wrapper">
      <div className="login-card">
        <h2 className="login-header">Welcome Back</h2>
        <p className="login-subtext">Log in to continue your cooking journey!</p>
        <form onSubmit={handleSubmit} className="login-form">
          <div className="login-field-wrapper">
            <input
              type="text"
              name="username"
              value={formData.username}
              onChange={handleChange}
              placeholder="Username"
              className={`login-field ${errors.username ? 'error' : ''}`}
            />
            {errors.username && <p className="error-text">{errors.username}</p>}
          </div>
          <div className="login-field-wrapper">
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Password"
              autoComplete="new-password"
              className={`login-field ${errors.password ? 'error' : ''}`}
            />
            {errors.password && <p className="error-text">{errors.password}</p>}
          </div>
          <button type="submit" className="login-submit">Log In</button>
        </form>
      </div>
    </div>
  );
};

export default LoginForm;
