import axios from 'axios';

const USER_API_URL = 'http://localhost:8081/api/users';
const WORKPLAN_API_URL = 'http://localhost:8081/api/workplans';
const POST_API_URL = 'http://localhost:8081/api/posts';

// User-related functions
export const getUser = (id) => axios.get(`${USER_API_URL}/${id}`).then(res => res.data);
export const updateUser = (id, user) => axios.put(`${USER_API_URL}/${id}`, user).then(res => res.data);
export const deleteUser = (id) => axios.delete(`${USER_API_URL}/${id}`);
export const followUser = (followerId, followingId) => axios.post(`${USER_API_URL}/${followerId}/follow/${followingId}`).then(res => res.data);
export const unfollowUser = (followerId, followingId) => axios.post(`${USER_API_URL}/${followerId}/unfollow/${followingId}`).then(res => res.data);
export const getFollowers = (id) => axios.get(`${USER_API_URL}/${id}/followers`).then(res => res.data);
export const getFollowing = (id) => axios.get(`${USER_API_URL}/${id}/following`).then(res => res.data);
export const getAllUsers = () => axios.get(USER_API_URL).then(res => res.data);
export const loginUser = (credentials) => axios.post(`${USER_API_URL}/login`, credentials).then(res => res.data);
export const createUser = (userData) => axios.post(USER_API_URL, userData).then(res => res.data);

// Work plan functions
export const createWorkPlan = (workPlan) => axios.post(WORKPLAN_API_URL, workPlan).then(res => res.data);
export const getWorkPlansByUser = (userId) => axios.get(WORKPLAN_API_URL, { params: { userId } }).then(res => res.data);
export const getWorkPlan = (id) => axios.get(`${WORKPLAN_API_URL}/${id}`).then(res => res.data);
export const updateWorkPlan = (id, workPlan) => axios.put(`${WORKPLAN_API_URL}/${id}`, workPlan).then(res => res.data);
export const deleteWorkPlan = (id) => axios.delete(`${WORKPLAN_API_URL}/${id}`);

// Post functions
export const createPost = (post) => axios.post(POST_API_URL, post).then(res => res.data);
export const getPostsByUserId = (userId) => axios.get(POST_API_URL, { params: { userId } }).then(res => res.data);
export const getPostById = (id) => axios.get(`${POST_API_URL}/${id}`).then(res => res.data);
export const updatePost = (id, post) => axios.put(`${POST_API_URL}/${id}`, post).then(res => res.data);
export const deletePost = (id) => axios.delete(`${POST_API_URL}/${id}`);

// ðŸ’¥ NEW: Get all posts
export const getAllPosts = () => axios.get(POST_API_URL).then(res => res.data);
