import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import Swal from 'sweetalert2';
import { getUser, updateUser, deleteUser, getFollowers, getFollowing, getPostsByUserId, deletePost } from '../services/api';
import FollowButton from './FollowButton';
import './UserProfile.css';

const UserProfile = () => {
  const navigate = useNavigate();
  const { userId } = useParams();
  const currentUserId = localStorage.getItem('userId');
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [viewPosts, setViewPosts] = useState(false);
  const [posts, setPosts] = useState([]);
  const [postsLoading, setPostsLoading] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    bio: '',
    profilePicture: '',
  });
  const [preview, setPreview] = useState(null);
  const [followers, setFollowers] = useState([]);
  const [following, setFollowing] = useState([]);
  const isOwnProfile = !userId || userId === currentUserId;

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const targetId = userId || currentUserId;
        if (!targetId) throw new Error('No user ID');
        const userData = await getUser(targetId);
        setUser(userData);
        setFormData({
          username: userData.username,
          email: userData.email,
          password: '',
          bio: userData.bio || '',
          profilePicture: userData.profilePicture || '',
        });
        setPreview(userData.profilePicture || null);
        setFollowers(await getFollowers(targetId));
        setFollowing(await getFollowing(targetId));
      } catch (error) {
        Swal.fire({ icon: 'error', title: 'Fetch Failed', text: 'User not found or please log in.' });
        navigate('/login');
      }
    };
    fetchUser();
  }, [navigate, userId, currentUserId]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 1024 * 1024) {
        Swal.fire({
          icon: 'error',
          title: 'File Too Large',
          text: 'Please upload an image under 1MB.',
        });
        return;
      }
      const reader = new FileReader();
      reader.onloadend = () => {
        const newImage = reader.result;
        setFormData({ ...formData, profilePicture: newImage });
        setPreview(newImage);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleUpdate = async () => {
    if (!isEditing) {
      setIsEditing(true);
      setViewPosts(false); // Reset to profile view when editing
      return;
    }
    try {
      const updatedUser = await updateUser(user.id, formData);
      setUser({
        ...updatedUser,
        profilePicture: updatedUser.profilePicture || formData.profilePicture,
      });
      setIsEditing(false);
      setFormData({
        username: updatedUser.username,
        email: updatedUser.email,
        password: '',
        bio: updatedUser.bio || '',
        profilePicture: updatedUser.profilePicture || formData.profilePicture,
      });
      setPreview(updatedUser.profilePicture || formData.profilePicture);
      Swal.fire({ icon: 'success', title: 'Updated!', text: 'Profile updated.', timer: 1500 });
    } catch (error) {
      console.error('Update error:', error);
      Swal.fire({ icon: 'error', title: 'Update Failed', text: 'Something went wrong.' });
    }
  };

  const handleDelete = async () => {
    const result = await Swal.fire({
      icon: 'warning',
      title: 'Are you sure?',
      text: 'This will delete your profile!',
      showCancelButton: true,
      confirmButtonText: 'Yes, delete it!',
    });
    if (result.isConfirmed) {
      try {
        await deleteUser(user.id);
        localStorage.removeItem('userId');
        Swal.fire({ icon: 'success', title: 'Deleted!', text: 'Profile deleted.', timer: 1500 });
        navigate('/');
      } catch (error) {
        Swal.fire({ icon: 'error', title: 'Delete Failed', text: 'Something went wrong.' });
      }
    }
  };

  const handleViewPosts = async () => {
    if (viewPosts) {
      setViewPosts(false); // Toggle back to profile view
      return;
    }
    setPostsLoading(true);
    setIsEditing(false); // Reset edit mode
    try {
      const response = await getPostsByUserId(currentUserId);
      setPosts(Array.isArray(response) ? response : []);
      setViewPosts(true);
    } catch (error) {
      console.error('Error fetching posts:', error);
      Swal.fire({
        icon: 'error',
        title: 'Fetch Failed',
        text: 'Unable to load posts. Please try again.',
      });
      setPosts([]);
    } finally {
      setPostsLoading(false);
    }
  };

  const handleEditPost = (postId) => {
    navigate(`/post/edit/${postId}`);
  };

  const handleDeletePost = async (postId) => {
    const result = await Swal.fire({
      icon: 'warning',
      title: 'Are you sure?',
      text: 'This will delete the post!',
      showCancelButton: true,
      confirmButtonText: 'Yes, delete it!',
    });
    if (result.isConfirmed) {
      try {
        await deletePost(postId);
        setPosts(posts.filter((post) => post.id !== postId));
        Swal.fire({
          icon: 'success',
          title: 'Deleted!',
          text: 'Post deleted.',
          timer: 1500,
        });
      } catch (error) {
        console.error('Error deleting post:', error);
        Swal.fire({
          icon: 'error',
          title: 'Delete Failed',
          text: 'Something went wrong.',
        });
      }
    }
  };

  const handleHome = () => navigate('/home');
  const handleCreateWorkPlan = () => navigate('/workplan/create');
  const handleViewWorkPlanList = () => navigate('/workplan/list');
  const handleCreatePost = () => navigate('/post/create');

  if (!user) return <div className="loading">Loading...</div>;

  return (
    <div className="profile-page">
      <div className="cover-photo">
        <div className="profile-picture-container">
          {user.profilePicture ? (
            <img src={user.profilePicture} alt="Profile" className="profile-picture" />
          ) : (
            <div className="profile-picture-placeholder">
              {user.username.charAt(0).toUpperCase()}
            </div>
          )}
        </div>
      </div>
      <div className="profile-content">
        <div className="profile-sidebar">
          <h3 className="sidebar-title">{user.username}</h3>
          {isOwnProfile && (
            <div className="sidebar-buttons">
              <button
                className={`sidebar-btn ${isEditing ? 'save' : 'edit'}`}
                onClick={handleUpdate}
              >
                {isEditing ? 'Save Changes' : 'Edit Profile'}
              </button>
              <button className="sidebar-btn delete" onClick={handleDelete}>
                Delete Profile
              </button>
              <button className="sidebar-btn create-workplan" onClick={handleCreateWorkPlan}>
                Create Work Plan
              </button>
              <button className="sidebar-btn view-workplan" onClick={handleViewWorkPlanList}>
                View Work Plan List
              </button>
              <button className="sidebar-btn create-post" onClick={handleCreatePost}>
                Share Cooking Skill Post
              </button>
              <button className="sidebar-btn my-posts" onClick={handleViewPosts}>
                {viewPosts ? 'Back to Profile' : 'My Posts'}
              </button>
              <button className="sidebar-btn home" onClick={handleHome}>
                Home
              </button>
            </div>
          )}
        </div>
        <div className="profile-main">
          {viewPosts ? (
            <div className="posts-section">
              <h2>Your Cooking Skill Posts</h2>
              {postsLoading ? (
                <div className="loading">Loading posts...</div>
              ) : posts.length === 0 ? (
                <p>No posts yet. Create one!</p>
              ) : (
                <div className="post-list">
                  {posts.map((post) => (
                    <div key={post.id} className="post-item">
                      {post.image && (
                        <img src={post.image} alt={post.title} className="post-item-image" />
                      )}
                      <div className="post-item-content">
                        <h3>{post.title}</h3>
                        <p>{post.description}</p>
                        <p className="post-date">
                          Created: {new Date(post.createdAt).toLocaleDateString()}
                        </p>
                      </div>
                      <div className="post-item-buttons">
                        <button
                          className="post-item-btn edit"
                          onClick={() => handleEditPost(post.id)}
                        >
                          Update
                        </button>
                        <button
                          className="post-item-btn delete"
                          onClick={() => handleDeletePost(post.id)}
                        >
                          Delete
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          ) : (
            <div className="profile-info">
              <h2>{user.username}'s Profile</h2>
              {isOwnProfile && isEditing ? (
                <div className="profile-edit-form">
                  <input
                    name="username"
                    value={formData.username}
                    onChange={handleChange}
                    placeholder="Username"
                    className="profile-input"
                  />
                  <input
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    placeholder="Email"
                    className="profile-input"
                  />
                  <input
                    name="password"
                    type="password"
                    value={formData.password}
                    onChange={handleChange}
                    placeholder="New Password (optional)"
                    className="profile-input"
                  />
                  <textarea
                    name="bio"
                    value={formData.bio}
                    onChange={handleChange}
                    placeholder="Tell us about yourself"
                    className="profile-textarea"
                  />
                  <input
                    type="file"
                    accept="image/*"
                    onChange={handleFileChange}
                    className="profile-file-input"
                  />
                  {preview && (
                    <img src={preview} alt="Preview" className="profile-image-preview" />
                  )}
                </div>
              ) : (
                <div className="profile-details">
                  <p><span className="label">Email:</span> {user.email}</p>
                  <p><span className="label">Bio:</span> {user.bio || 'No bio yet'}</p>
                  <div className="follow-stats">
                    <p><span className="label">Followers:</span> {followers.length}</p>
                    <p><span className="label">Following:</span> {following.length}</p>
                  </div>
                  {!isOwnProfile && (
                    <FollowButton currentUserId={currentUserId} targetUserId={user.id} />
                  )}
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
