import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import { getAllUsers, getFollowing, getAllPosts } from '../services/api';
import FollowButton from './FollowButton';
import './HomePage.css';

const HomePage = () => {
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [viewPosts, setViewPosts] = useState(false);
  const [posts, setPosts] = useState([]);
  const [postsLoading, setPostsLoading] = useState(false);
  const currentUserId = localStorage.getItem('userId');

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const allUsers = await getAllUsers();
        const otherUsers = allUsers.filter(user => user.id !== currentUserId);
        setUsers(otherUsers);
        setFilteredUsers(otherUsers);
      } catch (error) {
        console.error('Error fetching users:', error);
        Swal.fire({
          icon: 'error',
          title: 'Fetch Failed',
          text: 'Unable to load users.',
        });
      }
    };
    fetchUsers();
  }, [currentUserId]);

  useEffect(() => {
    const filtered = users.filter(user =>
      user.username.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setFilteredUsers(filtered);
  }, [searchQuery, users]);

  const handleExplorePosts = async () => {
    if (viewPosts) {
      setViewPosts(false); // Toggle back to People to Follow
      return;
    }
    setPostsLoading(true);
    try {
      const response = await getAllPosts();
      setPosts(Array.isArray(response) ? response : []);
      setViewPosts(true);
    } catch (error) {
      console.error('Error fetching posts:', error);
      console.error('Error response:', error.response?.data); // Log response data
      Swal.fire({
        icon: 'error',
        title: 'Fetch Failed',
        text: error.response?.data?.message || 'Unable to load posts.',
      });
      setPosts([]);
    } finally {
      setPostsLoading(false);
    }
  };

  const handleProfile = () => navigate('/profile');

  const handleLogout = () => {
    localStorage.removeItem('userId');
    navigate('/');
  };

  const viewProfile = (userId) => navigate(`/profile/${userId}`);

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  return (
    <div className="home-wrapper">
      <header className="home-header">
        <div className="header-logo">
          <span className="logo-icon">üç≥</span>
          <h1 className="logo-text">CookSphere</h1>
        </div>
        <nav className="header-nav">
          <input
            type="text"
            className="search-bar"
            placeholder="Search friends..."
            value={searchQuery}
            onChange={handleSearchChange}
          />
          <button className="nav-btn explore" onClick={handleExplorePosts}>
            {viewPosts ? 'People to Follow' : 'Explore'}
          </button>
          <button className="nav-btn profile" onClick={handleProfile}>
            Profile
          </button>
          <button className="nav-btn logout" onClick={handleLogout}>
            Logout
          </button>
        </nav>
      </header>

      {viewPosts ? (
        <section className="posts-section">
          <h2 className="posts-title">Explore Cooking Posts</h2>
          {postsLoading ? (
            <div className="loading">Loading posts...</div>
          ) : posts.length === 0 ? (
            <p>No posts available. Be the first to share!</p>
          ) : (
            <div className="post-list">
              {posts.map(post => (
                <div key={post.id} className="post-item">
                  {post.image && (
                    <img src={post.image} alt={post.title} className="post-item-image" />
                  )}
                  <div className="post-item-content">
                    <h3>{post.title}</h3>
                    <p>{post.description}</p>
                    <p className="post-date">
                      Posted by{' '}
                      <span
                        className="post-username"
                        onClick={() => viewProfile(post.userId)}
                      >
                        {post.username || 'Unknown User'}
                      </span>{' '}
                      on {new Date(post.createdAt).toLocaleDateString()}
                    </p>
                  </div>
                </div>
              ))}
            </div>
          )}
        </section>
      ) : (
        <>
          <section className="home-hero">
            <h2 className="hero-title">Your Cooking Community</h2>
            <p className="hero-subtitle">Follow chefs, share skills, and explore recipes.</p>
          </section>

          <section className="follow-section">
            <h3 className="follow-title">People to Follow</h3>
            <div className="follow-list">
              {filteredUsers.length > 0 ? (
                filteredUsers.map(user => (
                  <div key={user.id} className="follow-item">
                    <div className="user-info" onClick={() => viewProfile(user.id)}>
                      <div className="user-avatar">
                        {user.profilePicture ? (
                          <img src={user.profilePicture} alt={user.username} className="user-image" />
                        ) : (
                          <span>{user.username.charAt(0).toUpperCase()}</span>
                        )}
                      </div>
                      <span className="follow-name">{user.username}</span>
                    </div>
                    <FollowButton currentUserId={currentUserId} targetUserId={user.id} />
                  </div>
                ))
              ) : (
                <p>No users found matching your search.</p>
              )}
            </div>
          </section>
        </>
      )}

      <footer className="home-footer">
        <p>¬© 2025 CookSphere - Fueling Culinary Creativity</p>
      </footer>
    </div>
  );
};

export default HomePage;
