import axios from 'axios';

const API_URL = 'http://localhost:8081/api';

// User APIs
export const signup = (user) => axios.post(`${API_URL}/users`, user);
export const login = (credentials) => axios.post(`${API_URL}/users/login`, credentials);
export const getUser = (id) => axios.get(`${API_URL}/users/${id}`).then(res => res.data);
export const updateUser = (id, user) => axios.put(`${API_URL}/users/${id}`, user).then(res => res.data);
export const deleteUser = (id) => axios.delete(`${API_URL}/users/${id}`);
export const getFollowers = (id) => axios.get(`${API_URL}/users/${id}/followers`).then(res => res.data);
export const getFollowing = (id) => axios.get(`${API_URL}/users/${id}/following`).then(res => res.data);

// WorkPlan APIs
export const createWorkPlan = (workPlan) => axios.post(`${API_URL}/workplans`, workPlan);
export const getWorkPlansByUserId = (userId) => axios.get(`${API_URL}/workplans?userId=${userId}`);
export const getWorkPlanById = (id) => axios.get(`${API_URL}/workplans/${id}`);
export const updateWorkPlan = (id, workPlan) => axios.put(`${API_URL}/workplans/${id}`, workPlan);
export const deleteWorkPlan = (id) => axios.delete(`${API_URL}/workplans/${id}`);

// Post APIs
export const createPost = (post) => axios.post(`${API_URL}/posts`, post);
export const getPostsByUserId = (userId) => axios.get(`${API_URL}/posts?userId=${userId}`);
export const getPostById = (id) => axios.get(`${API_URL}/posts/${id}`);
export const updatePost = (id, post) => axios.put(`${API_URL}/posts/${id}`, post);
export const deletePost = (id) => axios.delete(`${API_URL}/posts/${id}`);
