import React from 'react';
import Swal from 'sweetalert2';
import './UserProfile.css'; // Import the new CSS file

const UserProfile = () => {
  const user = {
    username: 'chef1',
    email: 'chef1@example.com',
    bio: 'I love cooking!',
  };

  // Mock update function
  const handleUpdate = () => {
    Swal.fire({
      icon: 'info',
      title: 'Update Profile',
      text: 'This is a mock update. Add your API call here!',
      timer: 1500,
      showConfirmButton: false,
    });
    // To implement real update, call an API like: await updateUser(user.id, updatedData);
  };

  // Mock delete function
  const handleDelete = () => {
    Swal.fire({
      icon: 'warning',
      title: 'Are you sure?',
      text: 'This will delete your profile!',
      showCancelButton: true,
      confirmButtonText: 'Yes, delete it!',
      cancelButtonText: 'No, keep it',
    }).then((result) => {
      if (result.isConfirmed) {
        Swal.fire({
          icon: 'success',
          title: 'Deleted!',
          text: 'Your profile has been deleted (mock).',
          timer: 1500,
          showConfirmButton: false,
        });
        // To implement real delete, call an API like: await deleteUser(user.id);
        // Then navigate somewhere, e.g., navigate('/');
      }
    });
  };

  return (
    <div className="profile-wrapper">
      <div className="profile-card">
        <h2 className="profile-header">Your Profile</h2>
        <div className="profile-info">
          <p><strong>Username:</strong> {user.username}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Bio:</strong> {user.bio}</p>
        </div>
        <div className="profile-actions">
          <button className="profile-button update" onClick={handleUpdate}>
            Update
          </button>
          <button className="profile-button delete" onClick={handleDelete}>
            Delete
          </button>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
