import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import { getUser, updateUser, deleteUser } from '../services/api';
import './UserProfile.css';

const UserProfile = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    bio: '',
  });

  // Fetch user data on mount
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const userId = localStorage.getItem('userId'); // Get ID from login
        if (!userId) throw new Error('No user ID found');
        const userData = await getUser(userId);
        setUser(userData);
        setFormData({
          username: userData.username,
          email: userData.email,
          password: '', // Don’t prefill password for security
          bio: userData.bio,
        });
      } catch (error) {
        Swal.fire({
          icon: 'error',
          title: 'Fetch Failed',
          text: 'Could not load user data. Please log in again.',
        });
        navigate('/login');
        console.error('Error fetching user:', error);
      }
    };
    fetchUser();
  }, [navigate]);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle update
  const handleUpdate = async () => {
    if (!isEditing) {
      setIsEditing(true);
      return;
    }

    try {
      const updatedUser = await updateUser(user.id, formData);
      setUser(updatedUser);
      setIsEditing(false);
      Swal.fire({
        icon: 'success',
        title: 'Updated!',
        text: 'Your profile has been updated successfully.',
        timer: 1500,
        showConfirmButton: false,
      });
    } catch (error) {
      Swal.fire({
        icon: 'error',
        title: 'Update Failed',
        text: 'Something went wrong. Please try again.',
      });
      console.error('Error updating user:', error);
    }
  };

  // Handle delete
  const handleDelete = async () => {
    Swal.fire({
      icon: 'warning',
      title: 'Are you sure?',
      text: 'This will delete your profile permanently!',
      showCancelButton: true,
      confirmButtonText: 'Yes, delete it!',
      cancelButtonText: 'No, keep it',
    }).then(async (result) => {
      if (result.isConfirmed) {
        try {
          await deleteUser(user.id);
          localStorage.removeItem('userId'); // Clear user ID
          Swal.fire({
            icon: 'success',
            title: 'Deleted!',
            text: 'Your profile has been deleted.',
            timer: 1500,
            showConfirmButton: false,
          }).then(() => {
            navigate('/'); // Redirect to signup page
          });
        } catch (error) {
          Swal.fire({
            icon: 'error',
            title: 'Delete Failed',
            text: 'Something went wrong. Please try again.',
          });
          console.error('Error deleting user:', error);
        }
      }
    });
  };

  if (!user) return <div>Loading...</div>;

  return (
    <div className="profile-wrapper">
      <div className="profile-card">
        <h2 className="profile-header">Your Profile</h2>
        {isEditing ? (
          <div className="profile-form">
            <input
              type="text"
              name="username"
              value={formData.username}
              onChange={handleChange}
              placeholder="Username"
              className="profile-input"
            />
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Email"
              className="profile-input"
            />
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="New Password (optional)"
              className="profile-input"
            />
            <input
              type="text"
              name="bio"
              value={formData.bio}
              onChange={handleChange}
              placeholder="Bio"
              className="profile-input"
            />
          </div>
        ) : (
          <div className="profile-info">
            <p><strong>Username:</strong> {user.username}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Bio:</strong> {user.bio}</p>
          </div>
        )}
        <div className="profile-actions">
          <button className="profile-button update" onClick={handleUpdate}>
            {isEditing ? 'Save' : 'Update'}
          </button>
          <button className="profile-button delete" onClick={handleDelete}>
            Delete
          </button>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
