import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import { getPostsByUserId, deletePost } from '../services/api';
import './PostList.css';

const PostList = () => {
  const navigate = useNavigate();
  const currentUserId = localStorage.getItem('userId');
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await getPostsByUserId(currentUserId);
        setPosts(response.data);
        setLoading(false);
      } catch (error) {
        Swal.fire({
          icon: 'error',
          title: 'Fetch Failed',
          text: 'Unable to load posts.',
        });
        setLoading(false);
      }
    };
    fetchPosts();
  }, [currentUserId]);

  const handleEdit = (postId) => {
    navigate(`/post/edit/${postId}`);
  };

  const handleDelete = async (postId) => {
    const result = await Swal.fire({
      icon: 'warning',
      title: 'Are you sure?',
      text: 'This will delete the post!',
      showCancelButton: true,
      confirmButtonText: 'Yes, delete it!',
    });
    if (result.isConfirmed) {
      try {
        await deletePost(postId);
        setPosts(posts.filter((post) => post.id !== postId));
        Swal.fire({
          icon: 'success',
          title: 'Deleted!',
          text: 'Post deleted.',
          timer: 1500,
        });
      } catch (error) {
        Swal.fire({
          icon: 'error',
          title: 'Delete Failed',
          text: 'Something went wrong.',
        });
      }
    }
  };

  if (loading) return <div className="loading">Loading...</div>;

  return (
    <div className="post-list-container">
      <div className="post-list-card">
        <h2>Your Cooking Skill Posts</h2>
        {posts.length === 0 ? (
          <p>No posts yet. Create one!</p>
        ) : (
          <div className="post-list">
            {posts.map((post) => (
              <div key={post.id} className="post-item">
                {post.image && (
                  <img src={post.image} alt={post.title} className="post-item-image" />
                )}
                <div className="post-item-content">
                  <h3>{post.title}</h3>
                  <p>{post.description}</p>
                  <p className="post-date">
                    Created: {new Date(post.createdAt).toLocaleDateString()}
                  </p>
                </div>
                <div className="post-item-buttons">
                  <button
                    className="post-item-btn edit"
                    onClick={() => handleEdit(post.id)}
                  >
                    Update
                  </button>
                  <button
                    className="post-item-btn delete"
                    onClick={() => handleDelete(post.id)}
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
        <button
          className="post-list-back"
          onClick={() => navigate('/profile')}
        >
          Back to Profile
        </button>
      </div>
    </div>
  );
};

export default PostList;
