import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import { getUser, updateUser, deleteUser } from '../services/api';
import './UserProfile.css';

const UserProfile = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    bio: '',
  });

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const userId = localStorage.getItem('userId');
        if (!userId) throw new Error('No user ID found');
        const userData = await getUser(userId);
        setUser(userData);
        setFormData({
          username: userData.username,
          email: userData.email,
          password: '',
          bio: userData.bio || '',
        });
      } catch (error) {
        Swal.fire({
          icon: 'error',
          title: 'Fetch Failed',
          text: 'Could not load user data. Please log in again.',
        });
        navigate('/login');
        console.error('Error fetching user:', error);
      }
    };
    fetchUser();
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleUpdate = async () => {
    if (!isEditing) {
      setIsEditing(true);
      return;
    }
    try {
      const updateData = {
        username: formData.username,
        email: formData.email,
        bio: formData.bio,
        ...(formData.password && { password: formData.password }),
      };
      const updatedUser = await updateUser(user.id, updateData);
      setUser(updatedUser);
      setIsEditing(false);
      setFormData({ ...formData, password: '' });
      Swal.fire({
        icon: 'success',
        title: 'Updated!',
        text: 'Your profile has been updated successfully.',
        timer: 1500,
        showConfirmButton: false,
      });
    } catch (error) {
      Swal.fire({
        icon: 'error',
        title: 'Update Failed',
        text: 'Something went wrong. Please try again.',
      });
      console.error('Error updating user:', error);
    }
  };

  const handleDelete = async () => {
    Swal.fire({
      icon: 'warning',
      title: 'Are you sure?',
      text: 'This will delete your profile permanently!',
      showCancelButton: true,
      confirmButtonText: 'Yes, delete it!',
      cancelButtonText: 'No, keep it',
    }).then(async (result) => {
      if (result.isConfirmed) {
        try {
          await deleteUser(user.id);
          localStorage.removeItem('userId');
          Swal.fire({
            icon: 'success',
            title: 'Deleted!',
            text: 'Your profile has been deleted.',
            timer: 1500,
            showConfirmButton: false,
          }).then(() => {
            navigate('/');
          });
        } catch (error) {
          Swal.fire({
            icon: 'error',
            title: 'Delete Failed',
            text: 'Something went wrong. Please try again.',
          });
          console.error('Error deleting user:', error);
        }
      }
    });
  };

  const handleHome = () => {
    navigate('/home');
  };

  if (!user) return <div className="loading">Loading...</div>;

  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-header">
          <div className="profile-avatar">
            <span>{user.username.charAt(0).toUpperCase()}</span>
          </div>
          <h2>{user.username}'s Profile</h2>
        </div>
        {isEditing ? (
          <div className="profile-edit-form">
            <input
              type="text"
              name="username"
              value={formData.username}
              onChange={handleChange}
              placeholder="Username"
              className="profile-input"
            />
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Email"
              className="profile-input"
            />
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="New Password (optional)"
              className="profile-input"
            />
            <textarea
              name="bio"
              value={formData.bio}
              onChange={handleChange}
              placeholder="Tell us about yourself"
              className="profile-textarea"
            />
          </div>
        ) : (
          <div className="profile-details">
            <p><span className="label">Email:</span> {user.email}</p>
            <p><span className="label">Bio:</span> {user.bio || 'No bio yet'}</p>
          </div>
        )}
        <div className="profile-buttons">
          <button className={`profile-btn ${isEditing ? 'save' : 'edit'}`} onClick={handleUpdate}>
            {isEditing ? 'Save Changes' : 'Edit Profile'}
          </button>
          <button className="profile-btn delete" onClick={handleDelete}>
            Delete Profile
          </button>
          <button className="profile-btn home" onClick={handleHome}>
            Home
          </button>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
