import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import { createPost } from '../services/api';
import './PostForm.css';

const PostForm = () => {
  const navigate = useNavigate();
  const currentUserId = localStorage.getItem('userId');
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    image: '',
  });
  const [preview, setPreview] = useState(null);
  const [errors, setErrors] = useState({
    title: '',
    description: '',
    image: '',
  });

  const validateField = (name, value) => {
    switch (name) {
      case 'title':
        if (!value) return 'Title is required';
        if (value.length < 3) return 'Title must be at least 3 characters';
        return '';
      case 'description':
        if (!value) return 'Description is required';
        if (value.length < 10) return 'Description must be at least 10 characters';
        return '';
      case 'image':
        return '';
      default:
        return '';
    }
  };

  const validateForm = () => {
    const newErrors = {
      title: validateField('title', formData.title),
      description: validateField('description', formData.description),
      image: validateField('image', formData.image),
    };
    setErrors(newErrors);
    return !Object.values(newErrors).some((error) => error);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    setErrors({
      ...errors,
      [name]: validateField(name, value),
    });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 1024 * 1024) {
        Swal.fire({
          icon: 'error',
          title: 'File Too Large',
          text: 'Please upload an image under 1MB.',
        });
        return;
      }
      const reader = new FileReader();
      reader.onloadend = () => {
        const newImage = reader.result;
        setFormData({ ...formData, image: newImage });
        setPreview(newImage);
        setErrors({ ...errors, image: '' });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) {
      Swal.fire({
        icon: 'error',
        title: 'Validation Failed',
        text: 'Please fix the errors in the form.',
      });
      return;
    }
    try {
      const post = {
        ...formData,
        userId: currentUserId,
      };
      await createPost(post);
      Swal.fire({
        icon: 'success',
        title: 'Post Created!',
        text: 'Your post has been created.',
        timer: 1500,
      });
      navigate('/post/list');
    } catch (error) {
      Swal.fire({
        icon: 'error',
        title: 'Creation Failed',
        text: 'Something went wrong.',
      });
      console.error('Create post error:', error);
    }
  };

  const isFormValid = () => {
    return !Object.values(errors).some((error) => error) &&
           formData.title &&
           formData.description;
  };

  return (
    <div className="post-container">
      <div className="post-card">
        <h2>Create Cooking Skill Post</h2>
        <form onSubmit={handleSubmit} className="post-form">
          <div className="form-group">
            <input
              name="title"
              value={formData.title}
              onChange={handleChange}
              placeholder="Title (e.g., How to Bake Cupcakes)"
              className={`post-input ${errors.title ? 'error' : ''}`}
              required
            />
            {errors.title && <span className="error-message">{errors.title}</span>}
          </div>
          <div className="form-group">
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              placeholder="Description"
              className={`post-textarea ${errors.description ? 'error' : ''}`}
              required
            />
            {errors.description && <span className="error-message">{errors.description}</span>}
          </div>
          <div className="form-group">
            <input
              type="file"
              accept="image/*"
              onChange={handleFileChange}
              className="post-file-input"
            />
            {preview && (
              <img src={preview} alt="Preview" className="post-image-preview" />
            )}
            {errors.image && <span className="error-message">{errors.image}</span>}
          </div>
          <button
            type="submit"
            className="post-submit"
            disabled={!isFormValid()}
          >
            Create Post
          </button>
        </form>
        <button className="post-cancel" onClick={() => navigate('/profile')}>
          Cancel
        </button>
      </div>
    </div>
  );
};

export default PostForm;
