import React from 'react';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import { updateUser, deleteUser } from '../services/api'; // Import API functions
import './UserProfile.css';

const UserProfile = () => {
  const navigate = useNavigate();
  // Mock user data with an id (replace with real data from backend/login)
  const user = {
    id: '1', // Mock ID; in a real app, get this from login state or API
    username: 'chef1',
    email: 'chef1@example.com',
    bio: 'I love cooking!',
  };

  // Handle update
  const handleUpdate = async () => {
    try {
      // For now, we'll update with the same data (mock); in a real app, use a form to get new data
      await updateUser(user.id, {
        username: user.username,
        email: user.email,
        password: 'pass123', // Assuming password is required; adjust as needed
        bio: user.bio,
      });
      Swal.fire({
        icon: 'success',
        title: 'Updated!',
        text: 'Your profile has been updated successfully.',
        timer: 1500,
        showConfirmButton: false,
      });
    } catch (error) {
      Swal.fire({
        icon: 'error',
        title: 'Update Failed',
        text: 'Something went wrong. Please try again.',
      });
      console.error('Error updating user:', error);
    }
  };

  // Handle delete
  const handleDelete = async () => {
    Swal.fire({
      icon: 'warning',
      title: 'Are you sure?',
      text: 'This will delete your profile permanently!',
      showCancelButton: true,
      confirmButtonText: 'Yes, delete it!',
      cancelButtonText: 'No, keep it',
    }).then(async (result) => {
      if (result.isConfirmed) {
        try {
          await deleteUser(user.id);
          Swal.fire({
            icon: 'success',
            title: 'Deleted!',
            text: 'Your profile has been deleted.',
            timer: 1500,
            showConfirmButton: false,
          }).then(() => {
            navigate('/'); // Redirect to signup page after deletion
          });
        } catch (error) {
          Swal.fire({
            icon: 'error',
            title: 'Delete Failed',
            text: 'Something went wrong. Please try again.',
          });
          console.error('Error deleting user:', error);
        }
      }
    });
  };

  return (
    <div className="profile-wrapper">
      <div className="profile-card">
        <h2 className="profile-header">Your Profile</h2>
        <div className="profile-info">
          <p><strong>Username:</strong> {user.username}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Bio:</strong> {user.bio}</p>
        </div>
        <div className="profile-actions">
          <button className="profile-button update" onClick={handleUpdate}>
            Update
          </button>
          <button className="profile-button delete" onClick={handleDelete}>
            Delete
          </button>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
